mapper.select:
select count(*) from ${goods.drugStoreShortName}goods
where 1=1 

    and type1 = #{goods.type1} 


    and id like concat('%',#{goods.id},'%')


    and (barcode like concat('%',#{goods.barcode},'%')
    	or goodsname like concat('%',#{goods.barcode},'%')
    	or commonname like concat('%',#{goods.barcode},'%')
    	)


    and stock < #{goods.stock_search} 


    and barcode = #{goods.barcode_search} 


    and (goodsname like concat('%',#{goods.name_search},'%') OR commonname like concat('%',#{goods.name_search},'%'))

select id, barcode, goodsname, commonname, type1, type2, type3, dosageform, specifications, price, costprice, units, generatenum, generatedate, validityperiod, shelflife, stock, position, storage, pharmacyid, operator, operatedate, drugsid, drugscode, commonshotspell, commonnamespell, bxdygx, (select barcode2 from ${goods.drugStoreShortName}drugs a where a.barcode=b.barcode ) barcode2, (select manufactor from ${goods.drugStoreShortName}drugs a where a.barcode=b.barcode ) manufactor from ${goods.drugStoreShortName}goods b where 1=1
and type1 = #{goods.type1}
and id like concat('%',#{goods.id},'%')
and (barcode like concat('%',#{goods.barcode},'%') or goodsname like concat('%',#{goods.barcode},'%') or commonname like concat('%',#{goods.barcode},'%') )
and stock < #{goods.stock_search}
and barcode = #{goods.barcode_search}
and (goodsname like concat('%',#{goods.name_search},'%') OR commonname like concat('%',#{goods.name_search},'%'))
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}

select count(*) from ${goods.drugStoreShortName}goods where 1=1 and stock > 0 and exists (select 1 from ${goods.drugStoreShortName}drugs a where a.barcode = ${goods.drugStoreShortName}goods.barcode)
and type1 = #{goods.type1}
and id like concat('%',#{goods.id},'%')
and (barcode like concat('%',#{goods.barcode},'%') or goodsname like concat('%',#{goods.barcode},'%') or commonname like concat('%',#{goods.barcode},'%') or commonshotspell like concat('%',#{goods.barcode},'%') )
and stock < #{goods.stock_search}
and barcode = #{goods.barcode_search}
and (goodsname like concat('%',#{goods.name_search},'%') OR commonname like concat('%',#{goods.name_search},'%'))

select id, barcode, goodsname, commonname, type1, type2, type3, dosageform, specifications, price, costprice, units, generatenum, generatedate, validityperiod, shelflife, stock, position, storage, pharmacyid, operator, operatedate, drugsid, drugscode, commonshotspell, commonnamespell, bxdygx, (select barcode2 from ${goods.drugStoreShortName}drugs a where a.barcode=b.barcode ) barcode2, (select manufactor from ${goods.drugStoreShortName}drugs a where a.barcode=b.barcode ) manufactor from ${goods.drugStoreShortName}goods b where 1=1 and stock > 0 and exists (select 1 from ${goods.drugStoreShortName}drugs a where a.barcode = b.barcode)
and type1 = #{goods.type1}
and id like concat('%',#{goods.id},'%')
and (barcode like concat('%',#{goods.barcode},'%') or goodsname like concat('%',#{goods.barcode},'%') or commonname like concat('%',#{goods.barcode},'%') or commonshotspell like concat('%',#{goods.barcode},'%') )
and stock < #{goods.stock_search}
and barcode = #{goods.barcode_search}
and (goodsname like concat('%',#{goods.name_search},'%') OR commonname like concat('%',#{goods.name_search},'%'))
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}

select sum(stock) from ${goods.drugStoreShortName}goods
where 1=1 

    and type1 = #{goods.type1} 


    and id like concat('%',#{goods.id},'%')


    and barcode = #{goods.barcode }

mapper.insert:
insert into ${drugStoreShortName}goods(
drugsid, drugscode, commonshotspell, commonnamespell, bxdygx,
barcode, goodsname, commonname, type1, type2, type3, dosageform, specifications, price, costprice, units, generatenum, generatedate, validityperiod, shelflife, stock, position, storage, pharmacyid, operator, operatedate
)values( #{drugsid}, #{drugscode}, #{commonshotspell}, #{commonnamespell}, #{bxdygx},
#{barcode}, #{goodsname}, #{commonname}, #{type1}, #{type2}, #{type3}, #{dosageform}, #{specifications}, #{price}, #{costprice}, #{units}, #{generatenum}, #{generatedate}, #{validityperiod}, #{shelflife}, #{stock}, #{position}, #{storage}, #{pharmacyid}, #{operator}, #{operatedate,jdbcType=TIMESTAMP}
)

mapper.update:
update ${drugStoreShortName}goods
drugsid=#{drugsid}, drugscode=#{drugscode}, commonshotspell=#{commonshotspell}, commonnamespell=#{commonnamespell}, bxdygx=#{bxdygx},
barcode=#{barcode}, goodsname=#{goodsname}, commonname=#{commonname}, type1=#{type1}, type2=#{type2}, type3=#{type3}, dosageform=#{dosageform}, specifications=#{specifications}, price=#{price}, costprice=#{costprice}, units=#{units}, generatenum=#{generatenum}, generatedate=#{generatedate}, validityperiod=#{validityperiod}, shelflife=#{shelflife}, stock=#{stock}, position=#{position}, storage=#{storage}, pharmacyid=#{pharmacyid}, operator=#{operator}, operatedate=#{operatedate}
where id = #{id}

update ${drugStoreShortName}goods
drugsid=#{drugsid}, commonshotspell=#{commonshotspell}, commonnamespell=#{commonnamespell}, bxdygx=#{bxdygx},
goodsname=#{goodsname}, commonname=#{commonname}, type1=#{type1}, type2=#{type2}, type3=#{type3}, dosageform=#{dosageform}, specifications=#{specifications}, price=#{price}, costprice=#{costprice}, units=#{units}, generatenum=#{generatenum}, generatedate=#{generatedate}, validityperiod=#{validityperiod}, shelflife=#{shelflife}, stock=#{stock}, position=#{position}, storage=#{storage}, pharmacyid=#{pharmacyid}, operator=#{operator}, operatedate=#{operatedate}
where barcode = #{barcode}

mapper.delete:
	delete from ${goods.drugStoreShortName}goods 
	where 1=1 

        and id=#{goods.id}


        and barcode = #{goods.barcode }
