mapper.select:
    select 

    from ${orderdetail.drugStoreShortName}orderdetail 
    where 1=1 

        and createdate >= str_to_date(#{orderdetail.createdate_begin}, '%Y-%m-%d') 


        and createdate < date_add(str_to_date(#{orderdetail.createdate_end}, '%Y-%m-%d'), interval 1 day)


        and ordercode like concat('%',#{orderdetail.ordercode},'%')


        and id like concat('%',#{orderdetail.id},'%')


        and barcode = #{orderdetail.barcode } 


        and barcode = #{orderdetail.barcode_search} 


		order by #{page.sort} #{page.order}


		limit #{page.start},#{page.end}

select barcode,commonname, (select units from ${orderdetail.drugStoreShortName}drugs a where a.barcode=x.barcode) units, (select name from keyvalue b where type='units' and code = (select units from ${orderdetail.drugStoreShortName}drugs a where a.barcode=x.barcode) ) unitsname, (select specifications from ${orderdetail.drugStoreShortName}drugs a where a.barcode=x.barcode) specifications, sum(amount) total,sum(getmoney) amount from ${orderdetail.drugStoreShortName}orderdetail x where 1=1
and createdate >= str_to_date(#{orderdetail.createdate_begin}, '%Y-%m-%d')
and createdate <= date_add(str_to_date(#{orderdetail.createdate_end}, '%Y-%m-%d'), interval 1 day)
and ordercode like concat('%',#{orderdetail.ordercode},'%')
and id like concat('%',#{orderdetail.id},'%')
and barcode = #{orderdetail.barcode_search}
group by barcode,commonname
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}

select barcode,commonname,units,unitsname,specifications, sum(total) total,sum(amount) amount from ( select barcode,commonname, (select units from ${orderdetail.drugStoreShortName}drugs a where a.barcode=x.barcode) units, (select name from keyvalue b where type='units' and code = (select units from ${orderdetail.drugStoreShortName}drugs a where a.barcode=x.barcode) ) unitsname, (select specifications from ${orderdetail.drugStoreShortName}drugs a where a.barcode=x.barcode) specifications, amount total,getmoney amount from ${orderdetail.drugStoreShortName}orderdetail x where 1=1
and createdate >= str_to_date(#{orderdetail.createdate_begin}, '%Y-%m-%d')
and createdate <= date_add(str_to_date(#{orderdetail.createdate_end}, '%Y-%m-%d'), interval 1 day)
and ordercode like concat('%',#{orderdetail.ordercode},'%')
and id like concat('%',#{orderdetail.id},'%')
and barcode = #{orderdetail.barcode_search}
union select (select a.barcode from ${orderdetail.drugStoreShortName}drugs a where a.drugscode=x.yyxmbm) barcode, yyxmmc commonname, xmdj units, (select name from keyvalue b where type='units' and code = x.xmdj ) unitsname, xmgg specifications, xmsl total,xmje amount from ${orderdetail.drugStoreShortName}m_ybjsmxxx x where 1=1
and hjrq >= str_to_date(#{orderdetail.createdate_begin}, '%Y-%m-%d')
and hjrq <= date_add(str_to_date(#{orderdetail.createdate_end}, '%Y-%m-%d'), interval 1 day)
and exists (select 1 from ${orderdetail.drugStoreShortName}drugs a where a.drugscode=x.yyxmbm and a.barcode= #{orderdetail.barcode_search} )
) a group by barcode,commonname,units,unitsname,specifications
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}

mapper.update:
update sy_orderdetail
ordercode=#{ordercode}, detailcode=#{detailcode}, barcode=#{barcode}, goodsname=#{goodsname}, commonname=#{commonname}, createdate=#{createdate}, giftcode=#{giftcode}, price=#{price}, amount=#{amount}, money=#{money}, transportmoney=#{transportmoney}, iscomment=#{iscomment}, getmoney=#{getmoney}, paytype=#{paytype}, peybank=#{peybank}, remark=#{remark}, commentid=#{commentid}, isenough=#{isenough}, goodsid=#{goodsid}, units=#{units}
where id = #{id}
